export const Form = () => {

    const [formData, setFormData] = useState({
        username: "",
        age: "",
    });

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({
            ...formData,
            [name]: value,
        })


    }


    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData)
    }


    return <form onSubmit={handleSubmit}>
        <input name="username" onChange={handleChange} type="text" placeholder="enter the name"/>

        <input name="age" onChange={handleChange} type="number" placeholder="enter the age" />


        <input name="email" onChange={handleChange} type="number" placeholder="enter the age" />


        <input type="submit" value="Submit" />

    </form> 
}
